export default class Form extends Component{


	onInputChange = (e) => {
		this.setState({
			city: e.target.city.value
		});
	}

	getDataWeather = (e) =>{
		e.preventDefault();


	}


	render(){
		return(
			<form onSubmit={this.getDataWeather}>
				<input 
				type="text" 
				name="city" 
				placeholder="Введите город"
				onChange={this.onInputChange}
				/>
				<button>Получить погоду</button>
			</form>

		)
	}
}
