import React,{Component} from "react";


import Form from '../form/form';
import Weather from "../weather/weather";
import WeatherService from '../../services/weather-service';
import './app.css';



let city = 'London';
let appId = '887a9e3fcec30ecb5238bc4f492dcf5b';

class App extends React.Component {


	getDataWeather = async(e) => {
		e.preventDefault();
		const city = this.state.city;
		const api_call = await fetch(`http://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${appId}&units=metric`);
		const data = await api_call.json();
		console.log(data);

		if(data){
			this.setState({
				temperature: data.main.temp,
				city: data.name,

				humidity: data.main.humidity,
				description: data.weather[0].description,
				error: "" 
			});
		} 
	}

  render() {
    return (
      <div>
        <div className="wrapper">
          <div className="main">
            <div className="container">
              <div className="row">

                <div className="col-xs-7 form-container">
					{/* <Form /> */}
                  <Form getWeather={this.getWeather} />
                  <Weather getWeather={this.getWeather}/>

                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
};

export default App;


