import React, { Component } from 'react';

import './data-weather.css';

import Form from '../form/form';
import Weather from '../weather/weather';
import ErrorInc from '../error-inc/error-inc';

let appId = '887a9e3fcec30ecb5238bc4f492dcf5b';

export default class DataWeather extends Component {
	constructor(props){
		super(props);
		this.state={
			temp: null,
			city: null,
			humidity: null,
			description: null,
			wind: null,
			icon: null,
			error: null
		};
	};

	onInputChange = (e) => {
		this.setState({
			city: e.target.value
		});
	};

	componentDidCatch(){
		this.setState({
			error: true
		});
	}

	getDataWeather = async(e) => {
		e.preventDefault();
		const {city} = this.state;
		const result = await fetch(`http://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${appId}&units=metric`);
		if(!result.ok)
		// const response = await api_call.json();




		if (city) {
			this.setState({
				temp: response.main.temp,
				city: response.name,
				humidity: response.main.humidity,
				description: response.weather[0].description,
				wind: response.wind.speed,
				icon: response.weather[0].icon,
				error: null
			});
		} else {
			return <ErrorInc />;
		}
	};

	render() {

		const {temp,
			city,
			humidity,
			description,
			wind,
			icon,
			error} = this.state;

		const errorMessage = error ? <ErrorInc/> : null

		return (
			<div className="form_wrapper">
				<div className="form_container">
					<Form
						loadWeather={this.getDataWeather} 
						inputChange={this.onInputChange} />
					<Weather 
						temp={this.state.temp} 
						humidity={this.state.humidity}
						city={this.setState.city}
						description={this.state.description}
						wind={this.state.wind}
						icon={this.state.icon}
						error={this.state.error} />
           		</div>
			</div>


		)
	}
}
