import React, { Component } from 'react';

import WeatherService from '../../services/weather-service';

import './form.css';

export default class Form extends Component{

	weatherService = new WeatherService();

	constructor(props){
		super(props);
		this.state = {
			value: null,
			temp: null,
			city: null,
			country: null,
			sunrise: null,
			sunset: null,
			error: null
		}

	}

	getWeatherData (){
		this.weatherService()
			.then((city) =>{
				this.setState({
					value: city.value,
					temp: city.temp,
					// city: city.value,
					// country: country,
					sunrise: city.sunrise,
					sunset: city.sunset,
				});
			});
	}

	render(){
		return(
			<form onSubmit={this.props.getWeatherData}>
				<input type="text" value={this.state.name}name="city" placeholder="Введите город"/>
				<button className="btn">Получить погоду</button>
			</form>
		)
	}
}
