import React,{Component} from "react";
import Weather from "../weather/weather";


let appId = '887a9e3fcec30ecb5238bc4f492dcf5b';

export default class Form extends Component{

	constructor(props) {
		super(props);
		this.state = {
			city: '',
			temp: null
		};
	}

	onInputChange = (e) => {
		this.setState({
			city: e.target.value
		});

	}

	getDataWeather = async(e) => {
		e.preventDefault();
		const city = this.state.city;
		const api_call = await fetch(`http://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${appId}&units=metric`);
		const response = await api_call.json();
		this.setState({
			city: ''
		})
		console.log(response);
		if (city) {
			this.setState({
			temperature: response.main.temp,
			city: response.name,
			humidity: response.main.humidity,
			description: response.weather[0].description,
			wind: response.wind.speed,
			icon: response.weather[0].icon,
			error: ""
			});
		} else {
			this.setState({
			error: "Данные не введены , введите город"
			});
		}
	}



	render(){

		return(
			<form onSubmit={this.props.loadWeather}>
				<input type="text" placeholder="Введите город"
						onChange={this.onInputChange}
						value={this.state.city}
				/>
				<div><button>Получить погоду</button></div>
			</form>
		);
	}
}
