import React from "react";

import './weather.css';

const Weather = (props) => {
	return(

		<div className="weather__info">
			<React.Fragment>
				{
					props.city  && 
					<p className="weather__key"> Location: 
						<span className="weather__value"> { props.city }</span>
					</p>
				}  

				{ 	
					props.temp && 
					<p className="weather__key"> Температура: 
						<span className="weather__value"> { props.temp } &#8451;	</span>
					</p> 
				}

				{ 	
					props.humidity && 
					<p className="weather__key"> Влажность: 
						<span className="weather__value"> { props.humidity } &#37;</span>
					</p> 
				}

				{ 	
					props.icon && 
					<p className="weather__key"> Погода : 
						<span className="weather__value">
							<img src= {`http://openweathermap.org/img/w/${props.icon}.png`}
								alt="icon"
							/>
						</span>
					</p> 
				}

				{ 	
					props.wind && 
					<p className="weather__key"> &#127788; Ветер: 
						<span className="weather__value"> { props.wind} м/с</span>
					</p> 
				}


				{ 
					props.error && 
					<p className="weather__error">{ props.error }</p>  
				}

			</React.Fragment>
	 	</div>
	)

}

export default Weather;
