import React,{ Component } from 'react';


import WeatherService from '../../services/weather-service';
import Form from '../form/form';
import './select-data.css';

export default class SelectData extends Component{

	weatherService = new WeatherService();

	constructor(props){
		super(props);
		this.state = {
			value: null,
			temp: null,
			city: null,
			country: null,
			sunrise: null,
			sunset: null,
			error: null
		}

	}

	getWeatherData (){
		this.weatherService()
			.then((city) =>{
				this.setState({
					value: city.value,
					temp: city.temp,
					// city: city.value,
					// country: country,
					sunrise: city.sunrise,
					sunset: city.sunset,
				});
			});
	}

	render(){

		const {value,temp,city,country,sunrise,sunset} = this.state;

		return(

				<Form weatherHadler={this.getWeatherData}/>


		);

	}
}
