import React,{Component} from "react";

import './weather.css';

export default class Weather extends Component{

	constructor(props){
		super(props);
			this.state = {
				temp: null,
				humidity: null,
				city: null,
				description: null,
				wind: null,
				error: null

			}

	}




	// updateCountry(){
