import React, { Component } from 'react';

import Form from '../form/form';
import Weather from '../weather/weather';

let appId = '887a9e3fcec30ecb5238bc4f492dcf5b';

export default class DataWeather extends Component {
	constructor(props){
		super(props);
		this.state=({
			temperature: null,
			city: null,
			country: null,
			humidity: null,
			description: null,
			// wind: null,
			error: null
		});
	}

	getDataWeather = async(e) => {
		e.preventDefault();
		const city = this.state.city;
		const api_call = await fetch(`http://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${appId}&units=metric`);
		const data = await api_call.json();
		this.setState({
			city: ''
		});
		console.log(data);
			if (city) {
				this.setState({
				temperature: data.main.temp,
				city: data.name,
				humidity: data.main.humidity,
				description: data.weather[0].description,
				// wind: data.wind.speed,
				error: ""
				});
			} else {
			this.setState({
				temperature: undefined,
				city: undefined,
				country: undefined,
				humidity: undefined,
				description: undefined,
				wind: undefined,
				error: "Please enter the values."
				});
			}
	}




	render() {
		return (
			<div>
				 <div className="col-xs-7 form-container">
                  <Form getDataWeather={this.getDataWeather} />
                  <Weather 
                    temperature={this.state.temperature} 
                    humidity={this.state.humidity}
                    city={this.state.city}
                    country={this.state.country}
                    description={this.state.description}
					wind={this.state.wind}
                    error={this.state.error}
                  />
                </div>
			</div>
		)
	}
}
